from playsound import playsound
fibonacci = ""
pi = ""


def main():
    i = int(input("1.Pi \n2.Fibonacci \nPi or Fibonacci: "))
    if i == 1:
        for i in pi:
            print(i)
            playsound(i+".mp3")
    elif i == 2:
        for i in fibonacci:
            print(i)
            playsound(i+".mp3")
    else:
        pass


main()
